// @TOGGLES
// LIST FILTER
const listFilterToggleBtn = document.querySelector('.btn-active-filter-position');

const listFilter = document.querySelector('.list-filter');
const listFilterClassValue = 'filter-top';

const listFilterDiv = document.querySelector('.content-list > div');
const listFilterDivClassValue = 'flex-column';

if(listFilterToggleBtn != null) {
    listFilterToggleBtn.addEventListener('click', function () {
        setCookie('listFilterToggle', listFilter.classList.toggle(listFilterClassValue), monthInSecond);
        setCookie('listFilterToggle', listFilterDiv.classList.toggle(listFilterDivClassValue), monthInSecond);
    });
}

$(document).ready(function () {
    let isToggle = getCookie('listFilterToggle');
    if (listFilterToggleBtn != null && isToggle=='true') listFilter.classList.add(listFilterClassValue);
    if (listFilterToggleBtn != null && isToggle=='true') listFilterDiv.classList.add(listFilterDivClassValue);
});